mapper.select:

LEFT JOIN management.project_members pm ON p.id = pm.project_id


    AND (
    p.manager_id = #{currentUserId}
    OR (pm.user_id = #{currentUserId} AND pm.status = 'ACTIVE')
    )


        and p.id = #{id}


        and p.name like concat('%', #{name}, '%')


        and p.description like concat('%', #{description}, '%')





        and p.start_date >= #{startDate}


        and #{endDate} >= p.end_date



        and p.priority = #{priority}


        and p.status = #{status}




        and p.created_time between #{begin} and #{end}


GROUP BY p.id
order by p.created_time desc

select.where:
ORDER BY created_time DESC

    and id = #{id}


    and name like concat('%', #{name}, '%')


    and description like concat('%', #{description}, '%')



    and start_date >= #{startDate}


    and #{endDate} >= end_date



    and priority = #{priority}


    and status = #{status}




    and created_time between #{begin} and #{end}
