                and r.user_id = #{userId}




            AND r.user_id IN (
                SELECT pm.user_id
                FROM project_members pm
                JOIN projects p ON pm.project_id = p.id
                WHERE p.manager_id = #{currentUserId}
            )

                and r.user_id = #{userId}




            AND r.user_id = #{currentUserId}



        and r.report_type = #{reportType}



            AND r.submit_time BETWEEN #{begin} AND #{end}


            AND r.submit_time >= #{begin}


            AND r.submit_time <= #{end}



ORDER BY r.submit_time DESC
