mapper.select:
select count(*)
from tasks
where project_id = #{projectId}
  and assignee_id = #{userId}
  and status != 'completed'


LEFT JOIN project_members pm ON t.project_id = pm.project_id


    AND (
    t.project_id IN (SELECT id FROM projects WHERE manager_id = #{currentUserId})
    OR (pm.user_id = #{currentUserId} AND pm.status = 'ACTIVE')
    )

        and t.project_id = #{projectId}


        and t.id = #{Id}


        and t.title like concat('%', #{title}, '%')


        and t.assignee_id in (select id from users where real_name like concat('%', #{assigneeName}, '%'))


        and t.status = #{status}


        and t.status != 'CANCELLED'



            AND DATE(t.created_time) BETWEEN #{begin} AND #{end}


            AND DATE(t.created_time) >= #{begin}


            AND #{end} >= DATE(t.created_time)



GROUP BY t.id
ORDER BY t.created_time DESC




        and t.project_id = #{projectId}


        and t.id = #{Id}


        and t.title like concat('%', #{title}, '%')


        and t.assignee_id in (select id from users where real_name like concat('%', #{assigneeName}, '%'))


        and t.status = #{status}


        and t.status != 'CANCELLED'



            AND DATE(t.created_time) BETWEEN #{begin} AND #{end}


            AND DATE(t.created_time) >= #{begin}


            AND #{end} >= DATE(t.created_time)



ORDER BY t.created_time DESC







                and t.project_id = #{projectId}




            AND t.project_id IN (SELECT id FROM projects WHERE manager_id = #{currentUserId})

                and t.project_id = #{projectId}




            AND t.assignee_id = #{currentUserId}

                and t.project_id = #{projectId}




        and t.id = #{Id}


        and t.title like concat('%', #{title}, '%')


        and t.assignee_id in (select id from users where real_name like concat('%', #{assigneeName}, '%'))


        and t.status = #{status}


        and t.status != 'CANCELLED'



            AND DATE(t.created_time) BETWEEN #{begin} AND #{end}


            AND DATE(t.created_time) >= #{begin}


            AND #{end} >= DATE(t.created_time)



ORDER BY t.created_time DESC
